import React from 'react';
import {useForm} from 'react-hook-form'

function Register() {

  const { register, handleSubmit, formState, formState: { errors, submitCount }, watch } = useForm();
  const onSubmit = data => {
    console.log(data);
  }

  console.log(errors);


  return (
    <form onSubmit={ handleSubmit( onSubmit ) } autoComplete="off">
      <div className="form-group">
        <input type="text" {...register("username", {required: "This field is required.", maxLength: 100})} placeholder="Username" className="form-control"/>
        {errors.username && <div></div>}
      </div>
      <div className="form-group">
        <input type="text" {...register("email", {required: "This field is required.", pattern: {value: /^\S+@\S+$/i, message: "Please enter a valid email address"}})} placeholder="Email" className="form-control"/>
      </div>
      <div className="form-group">
        <input type="password" {...register("password", {required: "This field is required.", min: 3})} placeholder="Password" className="form-control"/>
      </div>
      <div className="form-group form-bottom">
        <input type="submit" name="Register" value="Register" className="btn btn-primary"/>
      </div>
    </form>
  );
}

export default Register;
